import React, { useEffect, useState } from 'react';
import { Card, CardContent, IconButton, Typography } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import ProductModal from '../../ProductModal/ProductModal';
import { Product, useData } from '../../../context/productContext';
import { useInit } from '../../../context/initContext';
import { useAuth } from '../../../context/authContext';

export interface PType {
    name: string,
    price: number,
    image: string,
    isFavorite: boolean,
}

const ProductCard = ({ product, handleBuy, isFavorite } : { product: Product, handleBuy: (id: number, price: number, quantity: number) => void, isFavorite: boolean} ) => {
    const [modal, setModal] = useState<boolean>(false);
    const{ isAuthenticated } = useAuth();
    const { doFavorite } = useInit();
    const { addFavoriteItem, deleteFavoriteItem} = useData();

  const handleCartClick = () => {
    setModal(true);
  };

  const handleClose = () => {
    setModal(false);
  }

  const handleFavoriteClick = () => {
    //doFavorite(product);
    if(isFavorite)
      deleteFavoriteItem(product.id);
    else
      addFavoriteItem(product.id);
  }

  useEffect(() => {
    console.log("Init Product Card.");
  }, [])

  return (
    <Card >
      <div style={{ position: 'relative'}}>
        { isAuthenticated ?
        <IconButton
          style={{ position: 'absolute', top: 10, right: 10, backgroundColor: 'white' }}
          onClick={handleFavoriteClick}
        >
          <FavoriteIcon color={isFavorite ? 'error' : 'action'} />
        </IconButton>
        : "" }
        <ImageWithFallback src={product.imgURL} alt={product.name} />
      </div>
      <CardContent style={{ position: 'relative' }}>
        <Typography variant="h6" component="div">
          {product.name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {product.price}â‚¬
        </Typography>
        <IconButton
          style={{ position: 'absolute', bottom: 10, right: 10, backgroundColor: 'white', border: '1px solid grey', padding: "15px" }}
          onClick={handleCartClick}
        >
          <AddShoppingCartIcon />
        </IconButton>
      </CardContent>
      <ProductModal product={product} open={modal} onClose={handleClose} handleBuy={(quantity: number) => handleBuy(product.id, product.price, quantity)}/>
    </Card>
  );
};


function ImageWithFallback({ src, alt }: {
  src: string,
  alt: string
}) {
  const [imgSrc, setImgSrc] = useState(src);

  const handleImgError = () => {
      // Setzen Sie hier den Pfad zu einem Ersatzbild oder einer Platzhaltergrafik
      setImgSrc("data:image/png;base64,");
  };

  return (
      <img 
          src={imgSrc} 
          alt={alt} 
          style={{ width: '100%' }} 
          onError={handleImgError} 
      />
  );
}
export default ProductCard;
